mapper.insert:
 	INSERT INTO instagram.feed_photo
		(
			feed_idx
			, file_name
			, file_path
		)
	VALUES
		(
			#{feed_idx}
			, #{file_name}
			, NULL
		);

 	INSERT INTO instagram.story_list
		(
			user_idx
			, regdate
			, use_yn
		)
	VALUES
		(
			#{user_idx}
			, NOW()
			, #{use_yn}
		)

 	INSERT INTO instagram.story_photo
		(
			story_idx
			, file_name
			, file_path
		)
	VALUES
		(
			#{story_idx}
			, #{file_name}
			, NULL
		)

 	INSERT INTO instagram.feed_like
		(
			feed_idx
			, user_idx
			, like_type
		)
	VALUES
		(
			#{feed_idx}
			, #{user_idx}
			, 1
		)

mapper.delete:
 	DELETE FROM instagram.feed_like
	WHERE feed_idx = #{feed_idx}
	  AND user_idx = #{user_idx}

mapper.insert:
 	INSERT INTO instagram.feed_reply
		(
			feed_idx
			, user_idx
			, feed_reply_contents
			, pidx
		)
	VALUES
		(
			#{feed_idx}
			, #{user_idx}
			, #{feed_reply_contents}
			, 0
		)

mapper.select:
SELECT COUNT(FEED_IDX) 
FROM FEED_LIST
WHERE USER_IDX = #{user_idx}

SELECT COUNT(FOLLOW_IDX)
FROM FOLLOW
WHERE FOLLOWING_USER_IDX = #{user_idx}

SELECT COUNT(FOLLOW_IDX)
FROM FOLLOW
WHERE FOLLOWER_USER_IDX = #{user_idx}

mapper.update:
UPDATE instagram.user_info
SET user_intro = #{user_intro}

		, user_name = #{user_name}


		, user_nickname = #{user_nickname}


		, user_pw = #{user_pw}

WHERE user_idx= #{user_idx}

